
public class ForTest02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<5;i++) {  //i=0 1 2 3 4 의 5회전 하는 반복문. 인덱스는 보통 0부터 시작하니까 왼쪽과 같이 코딩하는 것이 가독성이 높음. 
			for(int j=0;j<2;j++) {   // 이것은 두번 반복 for 문. 같은 동네에 사니까 i를 또 쓸 수는 없어 j로 함.
				System.out.println("안녕!");
			}
			System.out.println("잘가!");	
		}
			
		int sum=5;
		for(int a=1;a<=9;a++) {
			int result = sum * a;
			System.out.println(result);
				
				
					
				}
			
			
			
		

		// 구구단 5단을 출력하는 이중 for문을 작성하시오.
		//ex) 5*1=5
		//ex) 5*2=10
		//....
		//ex) 5*9=45
		
		
		// 구구단 2~9단을 출력하는 이중 for문을 작성하시오.
		//ex) 5*1=5
		//ex) 5*2=10
		//....
		//ex) 5*9=45
			
		}
		
		
		

	}

